var handleDataTableButtons=function(){
	"use strict";
	0!==$("#data-table").length&&$("#data-table").DataTable({
		"oLanguage": 
                { "sEmptyTable": "No hay registros disponibles",
                 "sInfo": "Hay _TOTAL_ registros. Mostrando de (_START_ a _END_)",
                 "sZeroRecords":    "No se encontro registros.",
                 "sInfoFiltered":   "(filtrado de _MAX_  registros en total)",
    			 "sInfoEmpty":      "Mostrando 0 a 0 de 0 registros",
                  "sLoadingRecords": "Por favor espera - Cargando...",
                   "sSearch": "Filtro:" ,
                   "sLengthMenu":"",
                   "stripeClasses": [ 'odd-row', 'even-row' ],
                    "oPaginate": { "sLast": "Última página",
                     "sFirst": "Primera",
                      "sNext": "Siguiente",
                       "sPrevious": "Anterior" }
                 } 
                ,
                /*"order": [[0, '']],*/
		dom:"Bfrtip",
		buttons:[
			{extend:"copy",className:"btn-sm btn-primary"},
			{extend:"csv",className:"btn-sm btn-primary"},
			{extend:"excel",className:"btn-sm btn-primary"},
			{extend:"pdf",className:"btn-sm btn-primary"},
			{extend:"print",className:"btn-sm btn-primary"}
		],
		responsive:!0
	})
  },
	TableManageButtons=function(){
		"use strict";
		return{
			init:function(){
				handleDataTableButtons()
			}
		}
	}();
	